(function ($) {

  // Code responsible to track partners.
  Drupal.behaviors.mrBookingTracking = {
    attach: function (context, settings) {
      $('body').once('coffee', function() {
        // Save the referrer to the cookie so it can be accessed by PHP code. Required because of Varnish.
        var indirectExists = /[?&]indirect=/.test(location.search);
        var referrerExists = typeof document.referrer !== 'undefined' && !Drupal.urlIsLocal(document.referrer);

        // Store the HTTP referrer only if there is no indirect code in the query string.
        if (!indirectExists && referrerExists) {
          $.cookie('http_referrer', document.referrer, {path: '/'});
        }
        // Clear the cookie if no longer needed.
        else {
          $.cookie('http_referrer', null, {path: '/'});
        }
      });
    }
  };

})(jQuery);
