/**
 * @file
 * JS to handle search pager second line display.
 */

Drupal.behaviors.seoPager = {
  attach: function(context, settings) {
    var ua = navigator.userAgent.toLowerCase();

    if ((ua.indexOf('mobile') >= 0 || ua.indexOf('tablet') >= 0)) {
      jQuery('.pager-wrapper .mr-pager-plus').click(function() {
        jQuery('.mr-pager-second-level-links').toggle();
      });
    }
    else {
      seoPagerHover();
    }

    // Replace spans with a tags.
    Drupal.replaceElementTag('.pager-wrapper span[attr-vsm]', '<a></a>');

    // Generate href tag from attr-vsm.
    jQuery('.pager-wrapper a[attr-vsm]').each(function() {
      jQuery(this).attr('href', atob(jQuery(this).attr('attr-vsm')));
      jQuery(this).removeAttr('attr-vsm');
    });
  }
};

function seoPagerHover() {
  jQuery('.pager-wrapper .mr-pager-plus').hover(function () {
    jQuery('.mr-pager-second-level-links').show();
  },
  function () {
    jQuery('.mr-pager-second-level-links').hover(function () {
      jQuery('.mr-pager-second-level-links').show();
    }, function () {
      jQuery('.mr-pager-second-level-links').hide();
    });
    jQuery('.pager-wrapper').mouseleave(function () {
      jQuery('.mr-pager-second-level-links').hide();
    });
  });
}
