/**
 * @file
 * Code to show/hide map on the result page.
 */

var searchMap = null;
var marker_pins = {};
var filterXHR = null;
var search_response = null;
var srp_query_string = '';

Drupal.behaviors.poiSearchResults = {
  attach: function (context, settings) {
    // Set appropriate value for document title.
    document.title = settings.seo.title;

    // Attach proper process to hide/show buttons.
    jQuery('body').once('srp-events', function() {
      // Bind the popstate event to handle history.back().
      window.onpopstate = function(e) {
        if (typeof e.state != 'undefined') {
          e.preventDefault();
          jQuery('body').addClass('manipulating-history');
          filter_hidden_form_set_state(e.state);
          filter_search_result('history');
        }
      };

      // Store the initial query string to JS variable.
      srp_query_string = settings.seo.query_string;

      // Set the appropriate URL based on the search filters.
      // We use replaceState for the first time as we don't want /restaurants
      // in the history.
      history.replaceState(filter_hidden_form_get_state(), null, settings.seo.path + srp_query_string);

      // Bind ajaxSend to cache the AJAX request in global variable.
      // Using this global variable we will abort existing requests when user
      // selects second filter before the first AJAX is completed.
      jQuery(document).ajaxSend(function (event, jqXHR, settings) {
        // Cache XHR object for current AJAX request.
        filterXHR = jqXHR;
      });

      jQuery('#panels-content-main-leftwrapper').on('click', '.btn-show-search-result-map', function (event) {
        event.preventDefault();
        jQuery('.panels-content-main-rightwrapper').show();
        jQuery('body').addClass('scroll-locked');
        jQuery('.michelin-map').addClass('mr-full-screen');
        // Try to load the map if not loaded already.
        Drupal.loadSearchResultMap();
      });

      jQuery('.michelin-map').on('click', '.btn-show-search-result-list', function (event) {
        event.preventDefault();
        jQuery('.panels-content-main-rightwrapper').hide();
        jQuery('body').removeClass('scroll-locked');
        jQuery('.michelin-map').removeClass('mr-full-screen');
      });

      jQuery('#panels-content-main-leftwrapper').on('mouseenter', '.poi-card-link', function() {
        var nid = parseInt(jQuery(this).attr('attr-id'));
        ft_b2c_poi_search_show_light_tooltip(nid);
      });

      jQuery('#panels-content-main-leftwrapper').on('mouseleave', '.poi-card-link', function() {
        var nid = parseInt(jQuery(this).attr('attr-id'));
        ft_b2c_poi_search_hide_light_tooltip(nid);
      });

      // Bind the pager links to load result on AJAX.
      jQuery('#panels-content-main-leftwrapper').on('click', '.mr-pager-link', function(event) {
        event.preventDefault();
        jQuery('form#ft-b2c-poi-search-filters-form input[name="page_number"]').val(jQuery(this).attr('attr-page-number'));
        filter_search_result('pager');
        var top_position = jQuery('.pane-ft-b2c-poi-search-ft-b2c-poi-search-result').position().top - jQuery('header').height() - 20;
        window.scrollTo(0, top_position);
      });
    });

    jQuery('.michelin-map').once('map', function () {
      var map_container = jQuery('.michelin-map');
      var id = jQuery(map_container).attr('id');

      // After the map has been initialized, we place the markers.
      jQuery('#' + id).bind('michelin-maps-event-onSuccess', function() {
        // Preparer Marker pins.
        Drupal.prepareMarkerPins();

        // Place all markers on the map.
        Drupal.placeMarkers();

        // Add auto-search control only if on desktop or tablet.
        if (typeof settings.auto_search_html != 'undefined' && Drupal.getBrowserType() != 'mobile') {
          searchMap.addHtmlControl('topright', 'michelin-maps-auto-search-control', settings.auto_search_html);
          // Enable checkbox by default if not in tablet.
          if (Drupal.getBrowserType() != 'tablet' && jQuery('#update-results-auto').hasClass('default-enabled')) {
            jQuery('#update-results-auto').prop('checked', true);
          }
        }

        // Add the caption control if caption markup present.
        if (typeof settings.caption_html != 'undefined') {
          searchMap.addHtmlControl('bottomright', 'michelin-maps-caption', settings.caption_html);
        }

        // Add the button "back to results" for mobile.
        if (typeof settings.back_to_results != 'undefined') {
          searchMap.addHtmlControl('topleft', 'michelin-maps-back', settings.back_to_results);
        }

        // Add the placeholder for mobile tooltip.
        searchMap.addHtmlControl('bottomleft', 'michelin-maps-mobile-tooltip', '');

        // Hide the container again after processing if it wasn't visible.
        jQuery('.panels-content-main-rightwrapper.preparing-map').removeClass('preparing-map').hide();
      });

      // When the map is moved, udpate the results.
      jQuery('#' + id).bind('michelin-maps-event-onMapMoved', function(event, bbox) {
        if (jQuery('#update-results-auto').is(':checked')) {
          Drupal.mapUpdated(bbox, true);
        }
        else if (jQuery('#update-results-auto').size() > 0) {
          // Update the details in hidden form.
          Drupal.mapUpdated(bbox, false);

          // Show the button to refresh.
          jQuery('.auto-search-enabled').hide();
          jQuery('.auto-search-disabled').show();
        }
      });

      // Show tooltip popup when mouseover.
      jQuery('#' + id).on('michelin-maps-event-marker-mouseover', function(event, id) {
        ft_b2c_poi_search_show_light_tooltip(id);
      });

      // Hide tooltip popup when mouseout.
      jQuery('#' + id).on('michelin-maps-event-marker-mouseout', function(event, id) {
        ft_b2c_poi_search_hide_light_tooltip(id);
      });

      // Change marker icon when clicked and show or hide tooltip.
      jQuery('#' + id).on('marker-clicked', function(event, id) {
        var row = Drupal.settings.search_result_markers[id];

        // If current marker is active marker, deactivate it.
        if (searchMap.activeMarkerId == id) {
          // Reset the current marker icon.
          Drupal.updateMarkerIcon(searchMap.activeMarkerId, true);

          // Set activate marker to blank.
          searchMap.activeMarkerId = '';

          // If in mobile, hide the bottom tooltip container.
          if (Drupal.getBrowserType() == 'mobile') {
            jQuery('.michelin-maps-mobile-tooltip').hide();
          }
          return;
        }

        if (searchMap.activeTooltipMarkerId == id) {
          searchMap.activeTooltipMarkerId = '';
          // Close the popup, so it will be opened again.
          searchMap.markers[id].closePopup();
        }

        // If active marker has some id, deactivate it.
        if (searchMap.activeMarkerId != '') {
          Drupal.updateMarkerIcon(searchMap.activeMarkerId, true);
          searchMap.activeMarkerId = '';
        }

        Drupal.updateMarkerIcon(id, false);

        // Set the current marker id as active marker to use later.
        searchMap.activeMarkerId = id;

        // If in mobile show the tooltip in bottom container.
        if (Drupal.getBrowserType() == 'mobile') {
          jQuery('.michelin-maps-mobile-tooltip').html(Drupal.settings.search_result_markers[id].content);
          jQuery('.michelin-maps-mobile-tooltip').show();
        }
        else {
          // Update popup content to tooltip.
          searchMap.markers[id].getPopup().setContent(row.content);

          // Scroll window to the corresponding POI card in desktop and tablet.
          var position = jQuery('a[attr-id="' + id + '"]').position();

          // Fixed top position (if admin_menu) + header height + 20px padding.
          var top_fixed = jQuery('header').position().top + jQuery('header').height() + 20;
          window.scrollTo(0, parseInt(position.top - (top_fixed)));
        }
      });

      // Bind the popupopen event of map.
      jQuery('#' + id).on('michelin-maps-event-popupopen', function(event, id) {
        // Disable search when popup is opened.
        searchMap.allowSearchUpdate = false;

        // Add active class to show hover effect on poi card.
        jQuery('a[attr-id="' + id + '"]').parents('li:first').addClass('active');
      });

      // Bind the popupclose event of map.
      jQuery('#' + id).on('michelin-maps-event-popupclose', function(event, id) {
        // Allow Search again.
        searchMap.allowSearchUpdate = true;

        // Remove active class to remove hover effect on poi card.
        jQuery('a[attr-id="' + id + '"]').parents('li:first').removeClass('active');

        // Empty the active marker id variable.
        searchMap.activeMarkerId = '';

        // Update the marker back to normal.
        Drupal.updateMarkerIcon(id, true);
      });

      // Hide Mobile tooltip.
      jQuery('#' + id).on('click', function(){
        if (Drupal.getBrowserType() == 'mobile') {
          Drupal.updateMarkerIcon(searchMap.activeMarkerId, true);
          jQuery('.michelin-maps-mobile-tooltip').hide();
        }
      });

      // Refresh the results and show the unchecked checkbox back.
      jQuery('#' + id).on('click', '#update-results-manual', function() {
        // We have already updated the form, just initiate the search.
        Drupal.mapUpdated(null, 1);

        // Update the display, hide button, show checkbox.
        jQuery('.auto-search-enabled').show();
        jQuery('.auto-search-disabled').hide();
      });

      // Try to load the map if not loaded already.
      Drupal.loadSearchResultMap();
    });
  }
};


Drupal.updateSearchResultPath = function () {
  if (jQuery('body').hasClass('manipulating-history')) {
    // We have pressed back/forward so no new entry in state.
    jQuery('body').removeClass('manipulating-history');
  }
  else {
    // Set the appropriate URL based on the search filters.
    // We use pushState to create new history entry everytime except for the
    // first time.
    history.pushState(filter_hidden_form_get_state(), null, Drupal.settings.seo.path + srp_query_string);
  }
}

/**
 * Helper function to prepare the marker pins/icons.
 */
Drupal.prepareMarkerPins = function () {
  for (i in Drupal.settings.map_pins) {
    if (typeof marker_pins[i] == 'undefined') {
      marker_pins[i] = {};
    }

    for (j in Drupal.settings.map_pins[i]) {
      marker_pins[i][j] = Drupal.michelinMapUtility.getIcon(Drupal.settings.map_pins[i][j]);
    }
  }
};

/**
 * Helper function to load the map.
 */
Drupal.loadSearchResultMap = function () {
  // Don't do anything if already loaded.
  if (jQuery('.michelin-map').hasClass('michelin-map-initialised')) {
    return;
  }

  // Don't load it yet, it's not visible on the page.
  if (!(jQuery('.michelin-map').is(':visible'))) {
    return;
  }

  var map_container = jQuery('.michelin-map');
  var id = jQuery(map_container).attr('id');
  var params = {};
  searchMap = new Drupal.michelinMap(id, params);
  searchMap.activeMarkerId = '';
  searchMap.activeTooltipMarkerId = '';
  searchMap.boundsUpdated = false;
  searchMap.loadMap();
};

/**
 * Helper function to place the markers on the map from data in settings.
 */
Drupal.placeMarkers = function() {
  if (searchMap && Drupal.settings.search_result_markers) {
    searchMap.hideOtherMarkers(Drupal.settings.search_result_markers);
    for (var i in Drupal.settings.search_result_markers) {
      var row = Drupal.settings.search_result_markers[i];

      // Light tooltip content.
      var light_tooltip = '<div class="poi_card-display-title light-tooltip">' + row.title + '</div>';

      // Prpare the light tooltip popup content.
      Drupal.settings.search_result_markers[i].light_tooltip = light_tooltip;

      try {
        // Define the appropriate icon to use with the POI.
        var icon = marker_pins['normal']['non-bookable'];
        if (row.bookable) {
          icon = marker_pins['normal']['bookable'];
        }

        // We don't display tooltip directly in mobile.
        if (Drupal.getBrowserType() == 'mobile') {
          searchMap.addMarker(row.id, row.xy, icon, row.title, '');
        }
        else {
          searchMap.addMarker(row.id, row.xy, icon, row.title, light_tooltip);
        }
      }
      catch (e) {
      }
    }

    // Update Map Bounds after adding markers.
    Drupal.updateMapBounds();

    // Load all tooltip content.
    for (var i in Drupal.settings.search_result_markers) {
      Drupal.loadTooltipContent(i);
    }
  }
};

/**
 * Helper function to load tooltip content for each marker.
 */
Drupal.loadTooltipContent = function(id) {
  // Return if we have already processed once.
  if (typeof Drupal.settings.search_result_markers[id].content != 'undefined') {
    return;
  }

  jQuery.ajax({
    cache: true,
    type: 'GET',
    dataType: 'text',
    global: false,
    url: Drupal.settings.search_result_markers[id].content_url,
    success: function (data) {
      // We add try catch just to ensure that by the time AJAX call finishes
      // we still have the search result available for the id.
      // See comments in MRV2-1313 for more details.
      try {
        Drupal.settings.search_result_markers[id].content = data;
      }
      catch (e) {
      }
    }
  });
}

/**
 * Update map bounds after marker pins load for the first time.
 */
Drupal.updateMapBounds = function () {
  // Update bounds on first time we get some result.
  if (!searchMap.boundsUpdated && Drupal.settings.search_result_markers) {
    // Added static in JS since this doesn't need to change.
    var updateBoundsConfig = {
      'paddingTopLeft': [100, 135],
      'paddingBottomRight': [100, 25],
      'maxZoom': 13
    };
    searchMap.updateBounds(updateBoundsConfig);

    // We don't want bounds to be updated again.
    searchMap.boundsUpdated = true;
  }
};

/**
 * Code to update results when map is zoomed or panned.
 */
Drupal.mapUpdated = function(bbox, submit) {
  if (bbox != null) {
    jQuery('form#ft-b2c-poi-search-filters-form input[name="bbox_ne_lat"]').val(bbox.ne.lat);
    jQuery('form#ft-b2c-poi-search-filters-form input[name="bbox_ne_lon"]').val(bbox.ne.lon);
    jQuery('form#ft-b2c-poi-search-filters-form input[name="bbox_sw_lat"]').val(bbox.sw.lat);
    jQuery('form#ft-b2c-poi-search-filters-form input[name="bbox_sw_lon"]').val(bbox.sw.lon);
  }

  if (submit) {
    // Integration with top search form.
    // Update the localisation to null since we are using map bounding box now.
    jQuery('#ft-b2c-poi-search-block-form input[name="text"]').val('').trigger('change');

    filter_search_result('map');
  }
};

/**
 * Code to update marker icon to default.
 */
Drupal.updateMarkerIcon = function (id, reset) {
  var markerType = (reset) ? 'normal' : 'active';
  var zIndex = (reset) ? 1 : 1000;
  var row = Drupal.settings.search_result_markers[id];
  try {
    // Define the appropriate icon to use with the POI.
    var icon = marker_pins[markerType]['non-bookable'];
    if (row.bookable) {
      icon = marker_pins[markerType]['bookable'];
    }
    searchMap.markers[id].setIcon(icon);
    searchMap.markers[id].setZIndexOffset(zIndex);
  }
  catch (e) {
  }

  // Unset corresponding poi card hover.
  if (Drupal.getBrowserType() != 'mobile') {
    if (reset) {
      jQuery('a[attr-id="' + id + '"]').removeClass('mr-result-active');
    }
    else {
      jQuery('a[attr-id="' + id + '"]').addClass('mr-result-active');
    }
  }
}

/**
 * Helper function to initiate search using hidden filter form.
 */
function filter_search_result(type) {
  // If clicked item is a facet filter, facets form is open and auto search is
  // disabled, we simply return.
  if (type == 'facet'
    && jQuery('.l-content').hasClass('facets-open')
    && jQuery('#invoke-search').hasClass('search-on-submit')) {
    jQuery('#invoke-search').addClass('facets-updated');
    return;
  }

  jQuery('#invoke-search').removeClass('facets-updated');

  // Check if an AJAX request is already in progress.
  if (filterXHR && jQuery('form#ft-b2c-poi-search-filters-form .form-submit').is(':disabled')) {
    // Abort the existing request.
    filterXHR.abort();
    // Enable the submit button to allow searching again.
    jQuery('form#ft-b2c-poi-search-filters-form .form-submit').prop('disabled', false);
  }

  // Start the search.
  jQuery('form#ft-b2c-poi-search-filters-form .form-submit').trigger('click');
}

/**
 * Helper function to get current hidden form values.
 */
function filter_hidden_form_get_state() {
  var state = {};
  var data = jQuery('#ft-b2c-poi-search-filters-form').serialize().split('&');
  for (i in data) {
    var row = data[i].split('=');
    // We don't want Drupal form fields.
    if (row[0] == 'form_build_id' || row[0] == 'form_id' || row[0] == 'form_token') {
      continue;
    }
    state[row[0]] = row[1];
  }
  return state;
}

/**
 * Helper function to update current hidden form values to one in arguments.
 */
function filter_hidden_form_set_state(state) {
  for (i in state) {
    jQuery('#ft-b2c-poi-search-filters-form input[name="' + i + '"]').val(state[i]);
  }

  // Update the price range again.
  Drupal.settings.price_filter.min_price = jQuery('form#ft-b2c-poi-search-filters-form input[name="min_price"]').val();
  Drupal.settings.price_filter.max_price = jQuery('form#ft-b2c-poi-search-filters-form input[name="max_price"]').val();
}

function ft_b2c_poi_search_show_light_tooltip(nid) {
  var poi_wrapper = jQuery('#panels-content-main-leftwrapper .poi-card-link[attr-id="' + nid + '"]');

  // No hover effects in mobile and tablet.
  if (Drupal.getBrowserType() == 'mobile' || Drupal.getBrowserType() == 'tablet') {
    return;
  }

  try {
    searchMap.allowSearchUpdateOriginal = searchMap.allowSearchUpdate;

    // Disable search.
    searchMap.allowSearchUpdate = false;

    // Get current type to show active marker-pin.
    var type = poi_wrapper.attr('attr-type');

    // Update marker pin to active.
    searchMap.markers[nid].setIcon(marker_pins['active'][type]);
    searchMap.markers[nid].setZIndexOffset(100);

    // We don't display the light tooltip for all markers if full tooltip
    // is visible.
    if (searchMap.activeMarkerId == '') {
      var row = Drupal.settings.search_result_markers[nid];

      // Update the content to show light tooltip.
      searchMap.markers[nid].getPopup().setContent(row.light_tooltip);

      // Open the popup.
      searchMap.markers[nid].openPopup();
    }

    // Add class to use later.
    poi_wrapper.addClass('mr-result-active');

    // Store the active tooltip marker id to check when clicking on the same.
    searchMap.activeTooltipMarkerId = nid;
  }
  catch (e) {
    // User might have hovered before map was fully loaded.
  }
}

function ft_b2c_poi_search_hide_light_tooltip(nid) {
  // No hover effects in mobile and table.
  if (Drupal.getBrowserType() == 'mobile' || Drupal.getBrowserType() == 'tablet') {
    return;
  }

  var poi_wrapper = jQuery('#panels-content-main-leftwrapper .poi-card-link[attr-id="' + nid + '"]');

  try {
    // Remove the class we added.
    poi_wrapper.removeClass('mr-result-active');

    // There is no active marker, just hide the light tooltip.
    if (searchMap.activeMarkerId == '') {
      // Close the light tooltip popup.
      searchMap.markers[nid].closePopup();
    }

    // Current marker is clicked, so keep it active.
    if (searchMap.activeMarkerId == nid) {
      return;
    }

    var type = poi_wrapper.attr('attr-type');

    // Update marker pin to normal.
    searchMap.markers[nid].setIcon(marker_pins['normal'][type]);
    searchMap.markers[nid].setZIndexOffset(1);

    // Remove the class we added.
    poi_wrapper.removeClass('mr-result-active');

    // Enable again if it was enabled.
    searchMap.allowSearchUpdate = searchMap.allowSearchUpdateOriginal;

    // Unset active tooltip marker nid.
    searchMap.activeTooltipMarkerId = '';
  }
  catch (e) {
    // User might have hovered before map was fully loaded.
  }
}

/**
 * Helper function - update search results and invoke required attach functions.
 */
function ft_b2c_poi_search_update_result() {
  if (search_response == null) {
    return;
  }

  // Update search result.
  jQuery('.pane-ft-b2c-poi-search-ft-b2c-poi-search-result .panel-pane-inside').html(search_response);

  // Initialise flex slider.
  Drupal.behaviors.flexsliderViewsSlideshow.attach(jQuery('.pane-ft-b2c-poi-search-ft-b2c-poi-search-result .panel-pane-inside'));

  // Initialise lazyloader.
  Drupal.behaviors.lazyloader.attach(jQuery('.pane-ft-b2c-poi-search-ft-b2c-poi-search-result .panel-pane-inside'));

  search_response = null;
}
