/**
 * Try to get the current location using HTML5 geolocation feature and process.
 */
function ft_b2c_poi_search_get_current_location() {
  try {
    if (navigator.geolocation) {
      var geoOptions = {timeout: 5000};

      // Use the browser feature to process the user's location.
      navigator.geolocation.getCurrentPosition(ft_b2c_poi_search_current_location_found, ft_b2c_poi_search_current_location_error, geoOptions);

      // Set the timoue separately to handle issues in FF.
      geoLocationTimeout = setTimeout(ft_b2c_poi_search_current_location_error, geoOptions.timeout);

      // Add icon to show processing to the user.
      jQuery('#ft-b2c-poi-search-block-form #edit-text').addClass('throbbing');

      // Make it readonly till the time we get response back from geolocation.
      jQuery('#ft-b2c-poi-search-block-form #edit-text').prop('readonly', true);
    }
  }
  catch (e) {
    jQuery('#ft-b2c-poi-search-block-form #edit-text').removeClass('throbbing');
  }
}

/**
 * Initiate the around me search if we find the location.
 */
function ft_b2c_poi_search_current_location_found(position) {
  // Add a placeholder in the "localisation" field.
  jQuery('#ft-b2c-poi-search-block-form #edit-text').val(Drupal.t('Around me'));

  // Make it editable again.
  jQuery('#ft-b2c-poi-search-block-form #edit-text').prop('readonly', false);

  // Remove the icon showing the ongoing process.
  jQuery('#ft-b2c-poi-search-block-form #edit-text').removeClass('throbbing');

  // Set the latitude and longitude in the hidden fields.
  jQuery('#ft-b2c-poi-search-block-form input[name="latitude"]').val(position.coords.latitude);
  jQuery('#ft-b2c-poi-search-block-form input[name="longitude"]').val(position.coords.longitude);
}

/**
 * Error callback for geolocation.getCurrentPosition.
 */
function ft_b2c_poi_search_current_location_error(error) {
  try {
    // Make it editable again.
    jQuery('#ft-b2c-poi-search-block-form #edit-text').prop('readonly', false);

    // Remove the icon showing the ongoing process.
    jQuery('#ft-b2c-poi-search-block-form #edit-text').removeClass('throbbing');

    // Clear the timeout if the function was called by user rejecting early.
    clearTimeout(geoLocationTimeout);
  }
  catch (e) {
  }
}
